const Yup = require("yup");
const formSchema = Yup.object({
  UserName: Yup.string()
    .required("UserName required")
    .min(6, "Username too short")
    .max(28, "Username too logn!"),
  PassWord: Yup.string()
    .required("UserName required")
    .min(6, "Username too short")
    .max(28, "Username too logn!"),
});
module.exports = { formSchema };
